        return new Promise((resolve, reject) => {
            const request = indexedDB.open('FuckChartsFileDB', 1)

            request.onerror = () => reject(request.error)
            request.onsuccess = () => {
                const db = request.result
                const transaction = db.transaction(['files'], 'readonly')
                const store = transaction.objectStore('files')
                const getRequest = store.get(fileId)

                getRequest.onsuccess = () => resolve(getRequest.result?.file)
                getRequest.onerror = () => reject(getRequest.error)
            }
        })
    }

    // 从缓存中删除文件
    async removeFromCache(fileId) {
        const currentFiles = this.getCachedFiles()
        const filteredFiles = currentFiles.filter(f => f.id !== fileId)
        localStorage.setItem(this.cacheKey, JSON.stringify(filteredFiles))

        // 同时从 IndexedDB 删除
        return new Promise((resolve) => {
            const request = indexedDB.open('FuckChartsFileDB', 1)
            request.onsuccess = () => {
                const db = request.result
                const transaction = db.transaction(['files'], 'readwrite')
                const store = transaction.objectStore('files')
                store.delete(fileId)
                resolve()
            }
        })
    }

    // 获取缓存大小
    getCacheSize() {
        const files = this.getCachedFiles()
        return files.reduce((sum, f) => sum + f.size, 0)
    }

    // 清空缓存
