    }

    try {
        const response = await fetch(`${API_BASE_URL}/files`)
        if (response.ok) {
            const backendFiles = await response.json()
            // 合并缓存文件和后端文件
            return [...cachedFiles, ...backendFiles.map(f => ({ ...f, status: 'uploaded' }))]
        }
    } catch (error) {
        console.error('获取后端文件列表失败:', error)
    }

    return cachedFiles
}

/**
 * 同步缓存文件到后端
 * @param {string} fileId - 要同步的文件ID
 * @returns {Promise} 同步结果
 */
export async function syncCachedFileToBackend(fileId) {
    const cachedFiles = fileCache.getCachedFiles()
    const cachedFile = cachedFiles.find(f => f.id === fileId)

    if (!cachedFile) {
        throw new Error('File not found in cache')
    }

    // 从 IndexedDB 获取原始文件
    const originalFile = await fileCache.getFileFromIndexedDB(fileId)
    if (!originalFile) {
        throw new Error('Original file not found')
    }

    const formData = new FormData()
    formData.append('file', originalFile)

    try {
        const response = await fetch(`${API_BASE_URL}/files/upload`, {
            method: 'POST',
            body: formData
        })

        if (!response.ok) {
            throw new Error('Sync failed')
        }

        const result = await response.json()

        // 更新缓存中的文件状态
        const updatedFiles = cachedFiles.map(f => 
            f.id === fileId ? { ...f, status: 'uploaded', backendId: result.fileId } : f
        )
        localStorage.setItem('fuck-charts-file-cache', JSON.stringify(updatedFiles))

        return result
    } catch (error) {
        console.error('同步文件到后端失败:', error)
        throw error
    }
}

/**
 * 批量同步所有缓存文件到后端
 * @returns {Promise} 同步结果
 */
export async function syncAllCachedFilesToBackend() {
    const cachedFiles = fileCache.getCachedFiles().filter(f => f.status === 'local')
    const results = []

    for (const file of cachedFiles) {
        try {
            const result = await syncCachedFileToBackend(file.id)
            results.push({ success: true, fileId: file.id, result })
        } catch (error) {
            results.push({ success: false, fileId: file.id, error: error.message })
        }
    }

    return results
}

/**
 * 检查后端连接状态
 * @returns {Promise<boolean>} 连接状态
 */
export async function checkBackendConnection() {
    return await fileCache.checkBackendConnection()
}

/**
 * 获取文件预览数据
 * @param {string} fileId - 文件ID
 * @returns {Promise} 文件预览数据
 */
export async function getFilePreview(fileId) {
    // 先检查是否为缓存文件
    const cachedFiles = fileCache.getCachedFiles()
    const cachedFile = cachedFiles.find(f => f.id === fileId)

    if (cachedFile && cachedFile.content) {
        return cachedFile.content
    }

    // 检查后端连接
    const isBackendConnected = await fileCache.checkBackendConnection()
    if (!isBackendConnected) {
        throw new Error('Backend not connected and no cached preview available')
    }

    try {
        const response = await fetch(`${API_BASE_URL}/files/${fileId}/preview`)
        if (!response.ok) {
            throw new Error('Failed to get preview')
        }
        return await response.json()
    } catch (error) {
        console.error('获取文件预览失败:', error)
        throw error
    }
}

/**
 * 从服务器或缓存删除文件
 * @param {string} fileId - 要删除的文件ID
 */
export async function deleteFile(fileId) {
    // 检查是否为缓存文件
    const cachedFiles = fileCache.getCachedFiles()
    const cachedFile = cachedFiles.find(f => f.id === fileId)

    if (cachedFile) {
        // 删除缓存文件
        await fileCache.removeFromCache(fileId)
        return { success: true, message: 'File removed from cache' }
    }

    // 尝试从后端删除
    try {
        const response = await fetch(`${API_BASE_URL}/files/${fileId}`, {
            method: 'DELETE'
        })
        if (!response.ok) {
            throw new Error('Delete failed')
        }
        return await response.json()
    } catch (error) {
        console.error('删除文件失败:', error)
        throw error
    }
}

/**
 * 重命名文件
 * @param {string} fileId - 文件ID
 * @param {string} newName - 新文件名
 */
export async function renameFile(fileId, newName) {
    // 检查是否为缓存文件
    const cachedFiles = fileCache.getCachedFiles()
    const cachedFileIndex = cachedFiles.findIndex(f => f.id === fileId)

    if (cachedFileIndex >= 0) {
        // 重命名缓存文件
        cachedFiles[cachedFileIndex].name = newName
        localStorage.setItem('fuck-charts-file-cache', JSON.stringify(cachedFiles))
        return { success: true, message: 'File renamed in cache' }
    }

    // 尝试在后端重命名
    try {
        const response = await fetch(`${API_BASE_URL}/files/${fileId}/rename`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ name: newName })
        })
        if (!response.ok) {
            throw new Error('Rename failed')
        }
        return await response.json()
    } catch (error) {
        console.error('重命名文件失败:', error)
        throw error
    }
}

/**
 * 获取缓存统计信息
 * @returns {Object} 缓存统计
 */
export function getCacheStats() {
    const files = fileCache.getCachedFiles()
    const size = fileCache.getCacheSize()
    return {
        fileCount: files.length,
        totalSize: size,
        formattedSize: formatFileSize(size),
        files: files.map(f => ({
            id: f.id,
            name: f.name,
            size: f.size,
            formattedSize: formatFileSize(f.size),
            uploadTime: f.uploadTime,
            status: f.status
        }))
    }
}

/**
 * 清空前端缓存
 * @returns {Promise} 清空结果
 */
export async function clearCache() {
    await fileCache.clearCache()
    return { success: true, message: 'Cache cleared' }
}

/**
 * 格式化文件大小
 * @param {number} bytes - 字节数
 * @returns {string} 格式化后的大小
 */
function formatFileSize(bytes) {
    if (bytes === 0) return '0 Bytes'
    const k = 1024
    const sizes = ['Bytes', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i]
}
