/* eslint-disable */

// 矩形树图图生成器
export default function treemapOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    return {
        title: {
            text: config.title || 'Chart of Treemap',
            subtext: config.subtext || '',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 16,
                fontWeight: 'bold',
            },
            subtextStyle: {
                fontSize: 12,
            },
        },
        tooltip: {},
        series: [
            {
                type: 'treemap',
                data: seriesData,
                layoutAlgorithm: 'squarify',
                visibleMin: config.visibleMin || 300, // 最小可见面积
                leafDepth: config.leafDepth || 1, // 叶子节点的深度
                label: {
                    show: true,
                    formatter: '{b}',
                },
            },
        ],
        ...customOption,
    }
}
