/* eslint-disable */

// 主题河流图配置生成器
export default function themeRiverOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    return {
        tooltip: {
            trigger: 'item',
            formatter: (params) => {
                const { name, value } = params.data;
                return `${name}: ${value}`;
            }
        },
        xAxis: {
            type: 'category',
            data: xData,
            boundaryGap: false
        },
        yAxis: {
            type: 'value'
        },
        series: [
            {
                type: 'themeRiver',
                data: seriesData
            }
        ],
        ...customOption
    };
}
