/* eslint-disable */

// 生成ECharts配置，支持多文件数据查找和主键/行号对齐
export function generateEChartOption(config, fileDataMap, xData, yDataArr, selectedChartType) {
    const { yAxis, title, colorScheme, animation } = config;
    // 类型安全处理，防止 xAxis/yAxis/series 被错误赋值为字符串 'all' 等
    let safeXAxis = config.xAxis;
    if (typeof safeXAxis !== 'object' || !safeXAxis) safeXAxis = { field: '', type: '' };
    let safeYAxis = yAxis;
    if (typeof safeYAxis !== 'object' && !Array.isArray(safeYAxis)) safeYAxis = { field: '', type: '' };
    let safeSeries = config.series;
    if (!Array.isArray(safeSeries)) safeSeries = [];

    // series配置
    const yArr = Array.isArray(safeYAxis) ? safeYAxis : [safeYAxis];
    const seriesArr = yArr.map((y, idx) => ({
        name: y.field,
        type: selectedChartType.value.toLowerCase(),
        data: yDataArr[idx],
        animationDuration: animation ? 1500 : 0,
        itemStyle: { color: getColorByScheme(colorScheme) }
    }));
    // 基础option
    const option = {
        title: {
            text: title || `${yArr.map(y => y.field).join(',')} vs ${safeXAxis.field}`,
            left: 'center',
            textStyle: { fontSize: 16, fontWeight: 'bold' }
        },
        tooltip: { trigger: 'axis' },
        legend: { data: yArr.map(y => y.field), top: 'bottom' },
        toolbox: {
            show: true,
            feature: {
                dataView: { show: true, readOnly: false },
                magicType: { show: true, type: ['line', 'bar'] },
                restore: { show: true },
                saveAsImage: { show: true }
            }
        },
        xAxis: {
            type: 'category',
            data: xData,
            axisLabel: { interval: 0, rotate: xData.length > 10 ? 45 : 0 }
        },
        yAxis: { type: 'value', name: yArr.map(y => y.field).join(',') },
        series: seriesArr,
        animation: animation,
        animationDuration: animation ? 1500 : 0
    };
    // 饼图特殊处理
    if (selectedChartType.value === 'Pie' || selectedChartType.value === 'pie') {
        option.series = [
            {
                name: yArr.map(y => y.field).join(','),
                type: 'pie',
                radius: '60%',
                data: xData.map((name, i) => ({ name, value: yDataArr[0][i] })),
                emphasis: {
                    itemStyle: {
                        shadowBlur: 10,
                        shadowOffsetX: 0,
                        shadowColor: 'rgba(0, 0, 0, 0.5)'
                    }
                }
            }
        ];
        delete option.xAxis;
        delete option.yAxis;
    }

    // 移除 dataRange、nullHandling 字段，防止污染 ECharts option
    if ('dataRange' in option) delete option.dataRange;
    if ('nullHandling' in option) delete option.nullHandling;
    // 调试输出
    console.log('[generateEChartOption] option:', option);
    return option;
}

// 根据配色方案获取颜色
export function getColorByScheme(scheme) {
    const colorSchemes = {
        default: '#5470c6',
        blue: '#1890ff',
        green: '#52c41a',
        warm: '#fa8c16',
        cool: '#13c2c2'
    }
    return colorSchemes[scheme] || colorSchemes.default
}
