import { ref, computed, watch, reactive, nextTick } from 'vue';
import { chartIcons } from '../../assets/JS/SVG/chartIcons.js';
import { chartsTooltipConfig } from '../../assets/JS/Config/ChartsTooltipConfig.js';
import { chartTypeConfig } from '../../assets/JS/Config/ChartTypeConfig.js';
import AutoRenderToggle from '../Common/AutoRenderToggle.vue';
import { getThemeIcon } from '@/assets/JS/SVG/icons.js';
import ChartMappingConfig from './ChartMappingConfig.vue';
import ChartFilterConfig from './ChartFilterConfig.vue';
import ChartAdvancedConfig from './ChartAdvancedConfig.vue';

// ÂΩìÂâçÁ±ªÂûãÁöÑÈÖçÁΩÆ
const chartHistoryKey = 'chartHistory';
export default {
  __name: 'ChartConfigPanel',
  props: {
    selectedChartType: {
      type: String,
      default: 'line'
    },
    currentFile: {
      type: Object,
      default: null
    }
  },
  emits: ['config-change', 'generate-chart'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();

    /* eslint-disable */
    const currentTypeConfig = computed(() => {
      // ÂÖºÂÆπÈ¶ñÂ≠óÊØçÂ§ßÂÜô/Â∞èÂÜô
      return chartTypeConfig[props.selectedChartType] || chartTypeConfig[props.selectedChartType.charAt(0).toUpperCase() + props.selectedChartType.slice(1)] || {};
    });

    // Props
    const props = __props;

    // Emits
    const emit = __emit;

    // ÂìçÂ∫îÂºèÊï∞ÊçÆ
    const showDataFilter = ref(false);
    const showAdvancedConfig = ref(false);
    const errorMessage = ref('');
    const tooltipRef = ref(null);

    // Ëá™Âä®Ê∏≤ÊüìÂºÄÂÖ≥ÔºåÈªòËÆ§ÊâãÂä®Ê∏≤Êüì
    const autoRender = ref(false);

    // ÊèêÁ§∫Ê°ÜÁä∂ÊÄÅ
    const tooltip = reactive({
      visible: false,
      type: '',
      description: '',
      dataRequirements: [],
      useCases: [],
      style: {}
    });
    let tooltipTimeout = null;

    // ÊãñÊãΩÁä∂ÊÄÅ
    const isDragOver = ref({
      xAxis: false,
      yAxis: false,
      series: false
    });
    const dragError = ref({
      xAxis: false,
      yAxis: false,
      series: false
    });

    // ÂõæË°®ÈÖçÁΩÆ
    const chartConfig = ref({
      xAxis: {
        field: '',
        type: ''
      },
      yAxis: {
        field: '',
        type: ''
      },
      series: [],
      title: '',
      colorScheme: 'default',
      animation: true,
      dataRange: 'all',
      nullHandling: 'ignore'
    });

    // ËÆ°ÁÆóÂ±ûÊÄß
    const isConfigValid = computed(() => {
      const xValid = chartConfig.value.xAxis && chartConfig.value.xAxis.field;
      const y = chartConfig.value.yAxis;
      const yValid = Array.isArray(y) ? y.length > 0 : y && y.field;
      return xValid && yValid;
    });

    // ÁõëÂê¨ chartConfig ÂèòÂåñÔºåËá™Âä®Ê∏≤Êüì
    watch(chartConfig, val => {
      if (autoRender.value && isConfigValid.value) {
        emit('generate-chart', chartConfig.value);
      }
      console.log('Current chartConfig:', JSON.parse(JSON.stringify(val)));
    }, {
      deep: true
    });

    // ÁõëÂê¨Ëá™Âä®Ê∏≤ÊüìÂºÄÂÖ≥ÂèòÂåñÔºåÂàáÊç¢‰∏∫ÂÆûÊó∂Êó∂Á´ãÂç≥Ê∏≤Êüì
    watch(autoRender, val => {
      if (val && isConfigValid.value) {
        emit('generate-chart', chartConfig.value);
      }
    });
    function getChartIcon(type) {
      // Áõ¥Êé•‰ΩøÁî®‰º†ÂÖ•ÁöÑÁ±ªÂûãÔºåchartIcons.js ‰∏≠ÁöÑÈîÆÊòØÈ¶ñÂ≠óÊØçÂ§ßÂÜôÊ†ºÂºè
      return chartIcons[type] || '';
    }
    function toggleDataFilter() {
      showDataFilter.value = !showDataFilter.value;
    }
    function toggleAdvancedConfig() {
      showAdvancedConfig.value = !showAdvancedConfig.value;
    }

    // ÊòæÁ§∫ÊèêÁ§∫Ê°Ü
    function showTooltip(event, type) {
      // Ê∏ÖÈô§‰πãÂâçÁöÑÂÆöÊó∂Âô®
      if (tooltipTimeout) {
        clearTimeout(tooltipTimeout);
      }

      // ËÆæÁΩÆ1.2ÁßíÂª∂Ëøü
      tooltipTimeout = setTimeout(() => {
        const config = chartsTooltipConfig[type];
        if (config) {
          tooltip.type = type;
          tooltip.description = config.description;
          tooltip.dataRequirements = config.dataRequirements;
          tooltip.useCases = config.useCases;
          tooltip.visible = true;

          // Âú®‰∏ã‰∏Ä‰∏™tick‰∏≠ËÆ°ÁÆó‰ΩçÁΩÆÔºåÁ°Æ‰øùDOMÂ∑≤Êõ¥Êñ∞
          nextTick(() => {
            positionTooltip(event);
          });
        }
      }, 1200); // 1.2ÁßíÂª∂Ëøü
    }

    // ÈöêËóèÊèêÁ§∫Ê°Ü
    function hideTooltip() {
      if (tooltipTimeout) {
        clearTimeout(tooltipTimeout);
        tooltipTimeout = null;
      }
      tooltip.visible = false;
    }

    // ËÆ°ÁÆóÊèêÁ§∫Ê°Ü‰ΩçÁΩÆÔºåÁ°Æ‰øù‰∏çË∂ÖÂá∫Â±èÂπï
    function positionTooltip(event) {
      if (!tooltipRef.value) return;
      const tooltipEl = tooltipRef.value;
      const rect = tooltipEl.getBoundingClientRect();
      const viewportWidth = window.innerWidth;
      const viewportHeight = window.innerHeight;
      let left = event.pageX + 10;
      let top = event.pageY + 10;

      // Èò≤Ê≠¢Âè≥ËæπË∂ÖÂá∫Â±èÂπï
      if (left + rect.width > viewportWidth) {
        left = event.pageX - rect.width - 10;
      }

      // Èò≤Ê≠¢Â∫ïÈÉ®Ë∂ÖÂá∫Â±èÂπï
      if (top + rect.height > viewportHeight) {
        top = event.pageY - rect.height - 10;
      }

      // Èò≤Ê≠¢Â∑¶ËæπË∂ÖÂá∫Â±èÂπï
      if (left < 0) {
        left = 10;
      }

      // Èò≤Ê≠¢È°∂ÈÉ®Ë∂ÖÂá∫Â±èÂπï
      if (top < 0) {
        top = 10;
      }
      tooltip.style = {
        left: `${left}px`,
        top: `${top}px`,
        position: 'fixed',
        zIndex: 1000
      };
    }

    // ÊãñÊãΩÂ§ÑÁêÜ
    function handleDragEnter(target) {
      isDragOver.value[target] = true;
      dragError.value[target] = false;
    }
    function handleDragLeave(target) {
      isDragOver.value[target] = false;
    }
    function handleDragOver(event, target) {
      event.preventDefault();
      isDragOver.value[target] = true;
    }
    function handleDrop(event, target) {
      event.preventDefault();
      isDragOver.value[target] = false;
      try {
        const dragData = JSON.parse(event.dataTransfer.getData('text/plain'));
        if (dragData.type === 'column') {
          const column = dragData.column;

          // È™åËØÅÊï∞ÊçÆÁ±ªÂûãÊòØÂê¶ÈÄÇÂêàÁõÆÊ†áÂå∫Âüü
          if (validateColumnForTarget(column, target)) {
            mapColumnToTarget(column, target);
            errorMessage.value = '';
          } else {
            dragError.value[target] = true;
            errorMessage.value = `Column "${column.name}" data type is not suitable for ${getTargetName(target)}`;
            setTimeout(() => {
              dragError.value[target] = false;
              errorMessage.value = '';
            }, 3000);
          }
        }
      } catch (error) {
        console.error('Drop failed:', error);
        errorMessage.value = 'Drag and drop failed, please try again';
      }
    }
    function validateColumnForTarget(column, target) {
      const columnType = column.type;
      switch (target) {
        case 'xAxis':
          // XËΩ¥ÈÄöÂ∏∏Êé•ÂèóÁ±ªÂà´ÂûãÊï∞ÊçÆ
          return ['string', 'category', 'date'].includes(columnType);
        case 'yAxis':
          // YËΩ¥ÈÄöÂ∏∏Êé•ÂèóÊï∞ÂÄºÂûãÊï∞ÊçÆ
          return ['number', 'integer'].includes(columnType);
        case 'series':
          // Á≥ªÂàóÊï∞ÊçÆÈÄöÂ∏∏Êé•ÂèóÊï∞ÂÄºÂûãÊï∞ÊçÆ
          return ['number', 'integer'].includes(columnType);
        default:
          return true;
      }
    }
    function mapColumnToTarget(column, target) {
      switch (target) {
        case 'xAxis':
          chartConfig.value.xAxis = {
            field: column.name,
            type: column.type
          };
          break;
        case 'yAxis':
          chartConfig.value.yAxis = {
            field: column.name,
            type: column.type
          };
          break;
        case 'series':
          // Á≥ªÂàóÂèØ‰ª•ÊúâÂ§ö‰∏™Ôºå‰ΩÜËøôÈáåÁÆÄÂåñ‰∏∫Âçï‰∏™
          chartConfig.value.series = [{
            field: column.name,
            type: column.type
          }];
          break;
      }
      emit('config-change', chartConfig.value);
    }
    function removeMapping(target) {
      switch (target) {
        case 'xAxis':
          chartConfig.value.xAxis = {
            field: '',
            type: ''
          };
          break;
        case 'yAxis':
          chartConfig.value.yAxis = {
            field: '',
            type: ''
          };
          break;
        case 'series':
          chartConfig.value.series = [];
          break;
      }
      emit('config-change', chartConfig.value);
    }
    function removeSeriesMapping(index) {
      chartConfig.value.series.splice(index, 1);
      emit('config-change', chartConfig.value);
    }
    function getTargetName(target) {
      const names = {
        xAxis: 'X Axis',
        yAxis: 'Y Axis',
        series: 'Data Series'
      };
      return names[target] || target;
    }
    function generateChart() {
      if (!isConfigValid.value) {
        errorMessage.value = 'Please complete the required data mapping configuration';
        return;
      }
      emit('generate-chart', chartConfig.value);
    }
    function resetConfig() {
      chartConfig.value = {
        xAxis: {
          field: '',
          type: ''
        },
        yAxis: {
          field: '',
          type: ''
        },
        series: [],
        title: '',
        colorScheme: 'default',
        animation: true,
        dataRange: 'all',
        nullHandling: 'ignore'
      };
      errorMessage.value = '';
      emit('config-change', chartConfig.value);
    }

    // ÁõëÂê¨ÂõæË°®Á±ªÂûãÂèòÂåñ
    watch(() => props.selectedChartType, newType => {
      // Ê†πÊçÆÂõæË°®Á±ªÂûãË∞ÉÊï¥ÈÖçÁΩÆ
      if (newType === 'Pie' || newType === 'pie') {
        // È•ºÂõæ‰∏çÈúÄË¶ÅYËΩ¥
        chartConfig.value.yAxis = {
          field: '',
          type: ''
        };
      }
      // Ëá™Âä®Ê∏≤ÊüìÂºÄÂêØÊó∂ÔºåÂàáÊç¢Á±ªÂûãÂêéÁ´ãÂç≥Ëß¶ÂèëÊ∏≤Êüì
      if (autoRender.value && isConfigValid.value) {
        emit('generate-chart', chartConfig.value);
      }
    });

    // ÂõæË°®ÂéÜÂè≤Â≠òÂÇ®
    function saveToHistory() {
      // eslint-disable-next-line
      const history = JSON.parse(localStorage.getItem(chartHistoryKey) || '[]');
      // Â≠òÂÇ®ÂΩìÂâçÈÖçÁΩÆÔºàÊ∑±Êã∑Ë¥ùÔºåÂ∏¶optionÂíåtitleÔºâ
      const item = {
        option: JSON.parse(JSON.stringify(chartConfig.value)),
        title: props.selectedChartType + ' ' + new Date().toLocaleString()
      };
      history.unshift(item);
      // ÊúÄÂ§ö‰øùÂ≠ò20Êù°
      if (history.length > 20) history.length = 20;
      localStorage.setItem(chartHistoryKey, JSON.stringify(history));
    }
    const __returned__ = {
      currentTypeConfig,
      props,
      emit,
      showDataFilter,
      showAdvancedConfig,
      errorMessage,
      tooltipRef,
      autoRender,
      tooltip,
      get tooltipTimeout() {
        return tooltipTimeout;
      },
      set tooltipTimeout(v) {
        tooltipTimeout = v;
      },
      isDragOver,
      dragError,
      chartConfig,
      isConfigValid,
      getChartIcon,
      toggleDataFilter,
      toggleAdvancedConfig,
      showTooltip,
      hideTooltip,
      positionTooltip,
      handleDragEnter,
      handleDragLeave,
      handleDragOver,
      handleDrop,
      validateColumnForTarget,
      mapColumnToTarget,
      removeMapping,
      removeSeriesMapping,
      getTargetName,
      generateChart,
      resetConfig,
      chartHistoryKey,
      saveToHistory,
      ref,
      computed,
      watch,
      reactive,
      nextTick,
      get chartIcons() {
        return chartIcons;
      },
      get chartsTooltipConfig() {
        return chartsTooltipConfig;
      },
      get chartTypeConfig() {
        return chartTypeConfig;
      },
      AutoRenderToggle,
      get getThemeIcon() {
        return getThemeIcon;
      },
      ChartMappingConfig,
      ChartFilterConfig,
      ChartAdvancedConfig
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
