/* eslint-disable */

// 树状图生成器
export default function treeOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    console.log('[TreeOption Gen] seriesData: ', seriesData);

    return {
        tooltip: {
            trigger: 'item',
            triggerOn: 'mousemove',
            formatter: function (params) {
                // 支持显示 value
                if (params.data.value !== undefined && params.data.value !== null) {
                    return `${params.data.name} : ${params.data.value}`;
                }
                return params.data.name;
            }
        },
        series: Array.isArray(seriesData)
            ? seriesData.map((tree, idx, arr) => {
                const n = arr.length;
                // 垂直均分 top/bottom
                const top = `${(idx * 100 / n + 5)}%`;
                const bottom = `${((n - idx - 1) * 100 / n + 5)}%`;
                return {
                    type: 'tree',
                    data: [tree],
                    top,
                    left: '10%',
                    bottom,
                    right: '10%',
                    symbolSize: 12,
                    label: {
                        position: 'left',
                        verticalAlign: 'middle',
                        align: 'right',
                        fontSize: 13
                    },
                    leaves: {
                        label: {
                            position: 'right',
                            verticalAlign: 'middle',
                            align: 'left'
                        }
                    },
                    expandAndCollapse: true,
                    animationDuration: 500,
                    animationDurationUpdate: 700,
                    initialTreeDepth: config.initialTreeDepth || 2,
                    roam: true
                };
            })
            : [
                {
                    type: 'tree',
                    data: seriesData,
                    top: '5%',
                    left: '10%',
                    bottom: '5%',
                    right: '10%',
                    symbolSize: 12,
                    label: {
                        position: 'left',
                        verticalAlign: 'middle',
                        align: 'right',
                        fontSize: 13
                    },
                    leaves: {
                        label: {
                            position: 'right',
                            verticalAlign: 'middle',
                            align: 'left'
                        }
                    },
                    expandAndCollapse: true,
                    animationDuration: 500,
                    animationDurationUpdate: 700,
                    initialTreeDepth: config.initialTreeDepth || 2,
                    roam: true
                }
            ],
        ...customOption,
    };
}
